package basics;

public class RelationaloperatorEx {
	

		public static void main(String[] args) {
			// TODO Auto-generated method stub
			
			int a=10;
			int b=20;
			int c=20;
			System.out.println("a > b "+(a>b));
			System.out.println("a < b "+(a<b));
			System.out.println("c >= b "+(c>=b));
			System.out.println("a <= b "+(a<=b));
			System.out.println("c == b "+(c==b));
			System.out.println("a != b "+(a!=b));

			

		}

	}


