package basics;

public class While {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Loop --> repeating the same code again and again until the condition fails
		 * 
		 */
		
		/*
		 * while(condition)
		 * {
		 * 
		 *	//condition pass
		 * }
		 */
		
		int i=1;
		
		while(i<10)
		{
			System.out.println("The value of is "+i);
			i++; //i=i+1, i=1+1, i=2
		}

	}

}



